import React, { useState } from "react"

export const Login = () => {

    const { email, setEmail } = useState('');
    const { pass, setPass } = useState('');

    const handleSubmit = (e) => {
        //reason why we use e.preventDefault is because we don't want the page to refresh on submit
        //which would cause the loss of the state and ultimately the data being submitted.
        e.preventDefault();
        console.log(email)
    }
    return (
        < form action="GET" onSubmit={handleSubmit}>
            <label htmlFor="email">Email</label>
            <input value={email} type="email" placeholder="example@email.com" id="email" name="email" />
            <label htmlFor="email">Password</label>
            <input value={pass} type="password" placeholder="*******" id="password" name="password" />
            <button>Log In</button>
        </form >
    )
}
